METADATA['en']['design'] = [{"category": "", "lang": "en", "title": "Design | Android Developers", "url": "design/", "image": "/design/media/index_landing_page.png", "tags": [], "summary": "About Android | Legal | Support", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Designing for Auto", "url": "design/auto/index.html", "image": "auto/images/ui/gearhead_overview.png", "tags": ["design", "auto"], "summary": "Building Apps for Auto Designing For Drivers Android Auto provide a standardized user interface and user interaction model that works across vehicles. As a designer, you do not need to worry about vehicle-specific hardware differences. This page describes\u2026", "keywords": ["design", "auto"], "type": "design"}, {"category": "", "lang": "en", "title": "Building Blocks | Android Developers", "url": "design/building-blocks/", "image": "/design/media/building_blocks_landing.png", "tags": [], "summary": "About Android | Legal | Support", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Android devices", "url": "design/devices.html", "image": "images/cards/android-devices_2x.jpg", "tags": [], "summary": "The device-centric UI principles, overviews, and detailed guidelines described here build on the core Android Design Principles to provide more specific design guidance for different form factors.\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Downloads | Android Developers", "url": "design/downloads/", "image": "/design/media/downloads_stencils.png", "tags": [], "summary": "Want everything? We&#39;ve bundled all the downloads available on Android Design into a single ZIP file.\nYou can also download individual files listed below.You may use these materials without restriction in your apps and to develop your apps. Download\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "tags": ["icons", "stencils", "colors", "swatches"], "url": "design/downloads/index.html", "image": "design/media/downloads_wear_toolkit.png", "title": "Downloads", "summary": "For icons, sticker sheets, and other downloadable resources, visit the Design site or use the links below. The toolkit contains detailed specs and measurements of all of the primary Android Wear UI components. Available in PDF and Illustrator formats.\u2026", "keywords": ["icons", "stencils", "colors", "swatches"], "type": "design"}, {"category": "", "lang": "en", "title": "Creative Vision", "url": "design/get-started/creative-vision.html", "image": "images/cards/design-creative-vision_2x.jpg", "tags": [], "summary": "Android design is shaped by three overarching goals for users that apply to apps as well as the system at large. As you work with Android, keep these goals in mind.\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Android Design Principles", "url": "design/get-started/principles.html", "image": "design/media/principles_delight.png", "tags": [], "summary": "These design principles were developed by and for the Android User Experience Team to keep users&#39; best interests in mind. For Android developers and designers, they continue to underlie the more detailed design guidelines for different types of devices\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Phones &amp; Tablets", "url": "design/handhelds/index.html", "image": "design/media/ui_overview_home_screen.png", "tags": [], "summary": "Android&#39;s system UI provides the framework on top of which you build your app, whether you&#39;re designing for phones, tablets, watches, or other form factors. Aspects of UI that are especially important for phones and tablets include the Home screen\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "tags": [], "url": "design/index.html", "image": "design/media/hero-material-design.png", "title": "Design", "summary": "Create beautiful experiences in your apps.", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Material Design for Android", "url": "design/material/index.html", "image": "images/cards/design-material-for-android_2x.jpg", "tags": ["material", "design"], "summary": "Material design is a comprehensive guide for visual, motion, and interaction design across platforms and devices. Android now includes support for material design apps. To use material design in your Android apps, follow the guidelines defined in the \u2026", "keywords": ["material", "design"], "type": "design"}, {"category": "", "lang": "en", "title": "Patterns | Android Developers", "url": "design/patterns/", "image": "/design/media/patterns_landing.png", "tags": [], "summary": "About Android | Legal | Support", "keywords": [], "type": "design"}, {"category": "", "keywords": ["actionbar", "navigation"], "lang": "en", "title": "Action Bar", "url": "design/patterns/actionbar.html", "image": "design/media/action_bar_pattern_overview.png", "tags": ["actionbar", "navigation"], "type": "design", "summary": "The Action bar is an essential design element for all apps. Learn about what the action bar can do and how to use it in your apps."}, {"category": "", "lang": "en", "title": "Backwards Compatibility", "url": "design/patterns/compatibility.html", "image": "design/media/compatibility_virtual_nav.png", "tags": ["support"], "summary": "Notes on how Android 4.x adapts UI designed for older hardware and OS versions.", "keywords": ["support"], "type": "design"}, {"category": "", "lang": "en", "title": "Confirming &amp; Acknowledging", "url": "design/patterns/confirming-acknowledging.html", "image": "design/media/confirm_ack_confirming.png", "tags": ["dialog", "toast", "notification"], "summary": "In some situations, when a user invokes an action in your app, it&#39;s a good idea to confirm or acknowledge that action through text. Confirming is asking the user to verify that they truly want to proceed with an action they just invoked. In some cases\u2026", "keywords": ["dialog", "toast", "notification"], "type": "design"}, {"category": "", "lang": "en", "title": "Full Screen", "url": "design/patterns/fullscreen.html", "image": "design/media/fullscreen_landing.png", "tags": ["full screen", "immersive", "leanback"], "summary": "Some content is best experienced full screen, like videos, games, image galleries, books, and slides in a presentation. You can engage users more deeply with content in full screen by minimizing visual distraction from app controls and protecting users\u2026", "keywords": ["full screen", "immersive", "leanback"], "type": "design"}, {"category": "", "lang": "en", "title": "Help", "url": "design/patterns/help.html", "image": "design/media/help_cling.png", "tags": ["settings", "preferences"], "summary": "This page covers design patterns for making help accessible in your app and tips for creating help content for users who are eager for assistance.\u2026", "keywords": ["settings", "preferences"], "type": "design"}, {"category": "", "lang": "en", "title": "Navigation with Back and Up", "url": "design/patterns/navigation.html", "image": "/design/media/navigation_between_siblings_gmail.png", "tags": ["navigation", "activity", "task", "up navigation", "back navigation"], "summary": "Consistent navigation is an essential component of the overall user experience. Few things frustrate users more than basic navigation that behaves in inconsistent and unexpected ways. Thoughtfully following the guidelines for Back and Up will make your\u2026", "keywords": ["navigation", "activity", "task", "up navigation", "back navigation"], "type": "design"}, {"category": "", "lang": "en", "title": "New in Android", "url": "design/patterns/new.html", "image": "images/cards/design-new-in-android_2x.jpg", "tags": ["kitkat", "android 4.4"], "summary": "A quick look at the new patterns and styles you can use to build beautiful Android apps\u2026 Material design is a comprehensive guide for visual, motion, and interaction design across platforms and devices. Android 5.0 provides a new theme, new widgets for\u2026", "keywords": ["kitkat", "android 4.4"], "type": "design"}, {"category": "", "lang": "en", "title": "Notifications, Android 4.4 and Lower", "url": "design/patterns/notifications_k.html", "image": "design/media/notifications_pattern_anatomy.png", "tags": [], "summary": "The notification system allows your app to keep the user informed about events, such as new chat messages or a calendar event. Think of notifications as a news channel that alerts the user to important events as they happen or a log that chronicles events\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Pure Android", "url": "design/patterns/pure-android.html", "image": "images/cards/design-pure-android_2x.png", "tags": [], "summary": "Most developers want to distribute their apps on multiple platforms. As you plan your app for Android, keep in mind that different platforms play by different rules and conventions. Design decisions that make perfect sense on one platform will look and\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Swipe Views", "url": "design/patterns/swipe-views.html", "image": "design/media/swipe_views.png", "tags": ["viewpager", "navigation", "tabs"], "summary": "Efficient navigation is one of the cornerstones of a well-designed app. While apps are generally built in a hierarchical fashion, there are instances where horizontal navigation can flatten vertical hierarchies and make access to related data items faster\u2026", "keywords": ["viewpager", "navigation", "tabs"], "type": "design"}, {"category": "", "lang": "en", "title": "Widgets", "url": "design/patterns/widgets.html", "image": "design/media/widgets_info.png", "tags": ["appwidget", "home"], "summary": "Design guide to creating widgets that are easy to use and look great.", "keywords": ["appwidget", "home"], "type": "design"}, {"category": "", "lang": "en", "title": "Style | Android Developers", "url": "design/style/", "image": "/design/media/design_elements_landing.png", "tags": [], "summary": "About Android | Legal | Support", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Devices and Displays", "url": "design/style/devices-displays.html", "image": "design/media/devices_displays_main.png", "tags": [], "summary": "Take advantage of Android's flexible layout system and create apps that gracefully scale from phones to tablets and beyond.", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Designing for Android TV", "url": "design/tv/index.html", "image": "design/tv/images/apps-games-rows.jpg", "tags": ["tv", "leanback", "designguidelines"], "summary": "Guidelines to help you create a great leanback experience on Android TV.", "keywords": ["tv", "leanback", "designguidelines"], "type": "design"}, {"category": "", "lang": "en", "title": "UI Patterns for TV", "url": "design/tv/patterns.html", "image": "design/tv/images/focus.png", "tags": [], "summary": "As a developer of apps for TV, you should follow certain patterns to enable users to quickly understand and operate your app. This section describes recommended design patterns for TV apps. Users typically navigate TV devices using a directional pad (\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Creative Vision for TV", "url": "design/tv/principles.html", "image": "", "tags": [], "summary": "Users bring a specific set of expectations when watching TV, versus interacting with a phone or tablet. The Android User Experience Team has developed the following guidelines for creation of the Android TV platform and the apps that run on it. The TV\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Style for TV", "url": "design/tv/style.html", "image": "design/tv/images/overscan.png", "tags": [], "summary": "Follow these style guidelines to create beautiful, functional apps for TV. The difference between a TV experience that feels right and one that does not greatly depends on the number, spacing, and size of on-screen elements. Although TV sizes and resolutions\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Videos | Android Developers", "url": "design/videos/", "image": "/design/media/extras_googleio_12.png", "tags": [], "summary": "The Android Design Team was pleased to present five fantastic design-oriented sessions at Google I/O 2012. Visit these pages to view the videos and presentations from the conference.You have a great idea for an Android app. You want it to stand out\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Context Awareness on Android Wear", "url": "design/wear/context.html", "image": "design/media/wear/ContextualExample.005.png", "tags": [], "summary": "Some of the most powerful user experiences with Android Wear are based on context-aware notifications and actions. By using device sensors and other contextual cues, your app can reveal information and functionality precisely when the user needs it, at\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Creative Vision for Android Wear", "url": "design/wear/creative-vision.html", "image": "design/media/wear/vision_traffic.png", "tags": [], "summary": "Android Wear devices provide just the right information at just the right time, allowing users to be more connected to both the virtual world and the real world. Great Android Wear experiences are: Most people are used to launching apps by clicking an\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "tags": [], "url": "design/wear/index.html", "image": "wear/images/screens/stream.gif", "title": "Designing for Android Wear", "summary": "Designing apps for wearable devices powered by Android Wear is substantially different than designing for phones or tablets: different strengths and weaknesses, different use cases, different ergonomics. To get started, you should understand the overall\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "UI Patterns for Android Wear", "url": "design/wear/patterns.html", "image": "design/media/wear/Bluebird.png", "tags": [], "summary": "Android Wear is used for micro-interactions, and so adhering to consistent design patterns that users are already accustomed to is paramount. Cards in the stream can take slightly different forms: App icons appear in a fixed position overhanging the edge\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Design Principles for Android Wear", "url": "design/wear/principles.html", "image": "design/media/wear/five_seconds.gif", "tags": [], "summary": "These design principles provide some simple heuristics about how you should plan and assess your Android Wear app design. A watch is a perfect form factor for a device that you can use while doing something else, such as cooking, eating, walking, running\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "App Structure for Android Wear", "url": "design/wear/structure.html", "image": "design/media/wear/bridgednotifications.jpg", "tags": [], "summary": "Users are used to tapping icons to launch apps. Android Wear is different. A typical Wear app adds a card to the stream at a contextually relevant moment. It might have a button that opens a full screen view for a fast micro interaction, but it just as\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Style for Android Wear", "url": "design/wear/style.html", "image": "design/media/wear/circle_message2.png", "tags": [], "summary": "Here are a number of design considerations to bear in mind that are particular to Android Wear. Be mindful of different device sizes and shapes. Wearable devices are a form of fashion and expression for their owners, and so Android Wear supports a variety\u2026", "keywords": [], "type": "design"}, {"category": "", "lang": "en", "title": "Watch Faces for Android Wear", "url": "design/wear/watchfaces.html", "image": "design/media/wear/Render_Next.png", "tags": [], "summary": "Creating Watch Faces Android Wear supports custom watch faces with designs that can show contextually relevant information to users. Designing a watch face requires a careful blending of data and visual elements that informs users without requiring close\u2026", "keywords": [], "type": "design"}];
