METADATA['en']['wear'] = [{"category": "wear preview", "keywords": ["wear"], "lang": "en", "title": "Preview API Overview", "url": "wear/preview/api-overview.html", "image": "images/cards/card-n-apis_2x.png", "tags": ["wear"], "type": "android", "summary": "Wear 2.0 is still in active development, but you can try it as part of the Wear 2.0 Developer Preview. The sections below highlight some of the new features for developers. The preview introduces powerful additions to the user interface, opening up exciting\u2026"}, {"category": "wear preview", "keywords": ["preview", "developer preview"], "lang": "en", "title": "Behavior Changes", "url": "wear/preview/behavior-changes.html", "image": "", "tags": ["preview", "developer preview"], "type": "android", "summary": "Along with new features, Android Wear 2.0 includes a variety of behavior changes. This document highlights some of the key changes to account for in your apps. If you have previously published an app for Android Wear, be aware that your app might be affected\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Download and Test with a Device", "url": "wear/preview/downloads.html", "image": "images/cards/card-n-downloads_2x.png", "tags": ["wear-preview"], "type": "android", "summary": "I have read and agree with the above terms and conditions You can run and test your app with the Android Wear 2.0 Preview in either of these ways: If you want an environment for basic compatibility testing of your app, you can use your current APK and\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "App Distribution", "url": "wear/preview/features/app-distribution.html", "image": "images/cards/card-n-sdk_2x.png", "tags": ["wear-preview"], "type": "android", "summary": "With Android Wear 2.0, a user can visit the Play Store on a watch and download a Wear app directly to the watch. Generally, a Wear 2.0 app in the Play Store needs a minimum and target API level of 24 or higher in the Android manifest file. The minimum\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Bridging Mode for Notifications", "url": "wear/preview/features/bridger.html", "image": "", "tags": ["wear-preview"], "type": "android", "summary": "By default, notifications are bridged (shared) from an app on a companion phone to the watch. If you build a standalone watch app and have a companion phone app, they may duplicate notifications. The Android Wear 2.0 Preview includes features to handle\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Watch Face Complications", "url": "wear/preview/features/complications.html", "image": "/wear/preview/images/complications-main-image.png", "tags": ["wear-preview"], "type": "android", "summary": "A complication is any feature in a watch face that displays more than hours and minutes . For example, a battery indicator is a complication. The Complications API is for both watch faces and data provider apps. Watch faces can display extra information\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Wrist Gestures", "url": "wear/preview/features/gestures.html", "image": "images/cards/card-n-sdk_2x.png", "tags": ["wear-preview"], "type": "android", "summary": "Wrist gestures can enable quick, one-handed interactions with your app when use of a touch screen is inconvenient. For example, a user can scroll through notifications with one hand while holding a cup of water with the other. Other examples of using \u2026"}, {"category": "wear preview", "keywords": ["wear"], "lang": "en", "title": "Wear Input Method Framework", "url": "wear/preview/features/ime.html", "image": "wear/preview/images/new_input_methods.png", "tags": ["wear"], "type": "android", "summary": "Wear 2.0 supports input methods beyond voice by extending the Android Input Method Framework (IMF) to Android Wear. IMF allows for virtual, on-screen keyboards and other input methods to be used for text entry. The IMF APIs used for Wear devices are the\u2026"}, {"category": "wear preview", "keywords": [], "lang": "en", "title": "Notification Changes in Android Wear 2.0", "url": "wear/preview/features/notifications.html", "image": "/wear/preview/images/expanded_diagram.png", "tags": [], "type": "android", "summary": "Android Wear 2.0 updates the visual style and interaction paradigm of notifications as well as introduces expanded notifications, which provide substantial additional content and actions in an app-like experience. The visual and interaction changes make\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Standalone Apps", "url": "wear/preview/features/standalone-apps.html", "image": "images/cards/card-n-sdk_2x.png", "tags": ["wear-preview"], "type": "android", "summary": "In Android Wear 2.0, apps can work independently of a phone. Users can complete more tasks on a watch, without access to an Android or iOS phone. A watch APK targeting Wear 2.0 should not be embedded in a phone APK. For more information, see App Distribution\u2026"}, {"category": "wear preview", "keywords": ["wear"], "lang": "en", "title": "Wear Navigation and Actions", "url": "wear/preview/features/ui-nav-actions.html", "image": "/wear/preview/images/card_drawer.png", "tags": ["wear"], "type": "android", "summary": "As part of the Material Design for Android Wear, Wear 2.0 adds interactive navigation and action drawers. The navigation drawer appears at the top of the screen and lets users jump to different views within the app, similar to the navigation drawer on\u2026"}, {"category": "wear preview", "keywords": ["wear"], "lang": "en", "title": "Curved Layout", "url": "wear/preview/features/wearable-recycler-view.html", "image": "/wear/preview/images/wrv_new.png", "tags": ["wear"], "type": "android", "summary": "Wear 2.0 introduces the WearableRecyclerView class for displaying and manipulating a vertical list of items optimized for round displays. WearableRecyclerView extends the existing RecyclerView class to provide a curved layout and a circular scrolling \u2026"}, {"category": "wear preview", "keywords": ["wear", "wear-preview"], "lang": "en", "title": "Android Wear 2.0 Developer Preview", "url": "wear/preview/index.html", "image": "wear/preview/images/hero-1x.png", "tags": ["wear", "wear-preview"], "type": "android", "summary": "Get ready for the next version of Android Wear!\n          Support stand-alone Wear devices and apps.\n          Create enhanced user interaction and glanceable experiences .\n          Test your apps on Wear devices ."}, {"category": "wear preview", "keywords": [], "lang": "en", "title": "License Agreement", "url": "wear/preview/license.html", "image": "", "tags": [], "type": "android", "summary": "To get started with the Android Wear SDK Preview, you must agree to the following terms and conditions. As described below, please note that this is a preview version of the Android Wear SDK, subject to change, that you use at your own risk. The Android\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Android Wear Preview Program Overview", "url": "wear/preview/program.html", "image": "images/cards/card-n-sdk_2x.png", "tags": ["wear-preview"], "type": "android", "summary": "Welcome to the Android Wear 2.0 Developer Preview! This program gives you everything you need to get started with the next version of Android Wear. It\u2019s free, and you can get started right away by downloading the Developer Preview resources. Run and test\u2026"}, {"category": "wear preview", "keywords": ["wear-preview"], "lang": "en", "title": "Get Started with the Preview", "url": "wear/preview/start.html", "image": "images/cards/card-n-sdk_2x.png", "tags": ["wear-preview"], "type": "android", "summary": "If you want an environment for basic compatibility testing, you can use your current APK and a supported watch or an emulator. You don&#39;t necessarily need to update your full development environment to do basic testing. To simply test your app&#39;\u2026"}, {"category": "wear preview", "keywords": ["preview", "developer preview"], "lang": "en", "title": "Support and Release Notes", "url": "wear/preview/support.html", "image": "", "tags": ["preview", "developer preview"], "type": "android", "summary": "If you experience problems when developing and testing with the Wear 2.0 Developer Preview, please file bugs at https:&#47;&#47;g.co&#47;wearpreviewbug . To discuss issues or ideas with other developers working with Android Wear, join the Wear Developer\u2026"}, {"category": "wear", "keywords": [], "lang": "en", "title": "Android Wear", "url": "wear/index.html", "image": "images/cards/android-wear_2x.png", "tags": [], "type": "android", "summary": "Small, powerful devices, worn on the body. Useful information when you need it most. Intelligent answers to spoken questions. Tools to help reach fitness goals. Your key to a multiscreen world. Before you start building, check out the Android Wear Design\u2026"}];
